sys_variable_value.value:
(function execute(inputs, outputs) {

   var fields= new sn_apm_sap_int.ApmSapUtils().getCurrencyFields('sn_shop_invoice_line',inputs.sysid.toString());
  outputs.currency_fields=fields;

  if(inputs.invoiceRec.invoice.purchase_order.blanket_order){
    outputs.erp_po_number = inputs.invoiceRec.invoice.purchase_order.blanket_order.erp_number;
    outputs.erp_po_line_number = new sn_apm_sap_int.ApmSapUtils().getERPblanketLineNumberForIR(inputs.invoiceRec);
  }else{
    outputs.erp_po_number = inputs.invoiceRec.invoice.purchase_order.erp_number;
    outputs.erp_po_line_number = inputs.invoiceRec.order_line.erp_line_number;
  }

})(inputs, outputs);

