sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var invoiceLine=JSON.parse(inputs.json);

  if(inputs.irlRec.order_line.product_type == 'handling_fee')
    invoiceLine['PO_UNIT'] = 'AU';

  var mandatoryFieldsList = {
      "Invoice > Purchase Order > ERP Number": invoiceLine.PO_NUMBER,
      "Invoice Line > Purchase Order Line > ERP Line Number": invoiceLine.PO_ITEM,
      "Invoice Line > Line Amount Invoiced": invoiceLine.ITEM_AMOUNT,
      "Invoice Line > Invoiced Quantity": invoiceLine.QUANTITY,
      "Invoice Line > Purchase Order Line > Unit > Code": invoiceLine.PO_UNIT,
    };
    var emptyFields = new sn_apm_sap_int.ApmSapUtils().checkMandatoryFields(mandatoryFieldsList);
    if (!gs.nil(emptyFields)) {
      throw new Error('Mandatory field(s) '.concat(emptyFields)+' cannot be blank.');
    }
  outputs.invoice_line=invoiceLine;
})(inputs, outputs);

