sys_variable_value.value:
(function execute(inputs, outputs) {
	var grec = new GlideRecord('sn_shop_invoice_line');
	grec.addQuery("invoice.sys_id", inputs.psm_invoice.sys_id.toString());
	grec.orderBy('number');
	grec.query();

	if (!(grec.hasNext())) {
		throw new Error("Invoice should have atleast one Invoice Line");
	} else {
		//Creating Invoice Header Object
		outputs.invoice_header = new sn_apm_sap_int.ApmSapUtils().executeAction('sn_apm_sap_int.generate_hana_invoice_header_object', {
			invoice_header: inputs.psm_invoice
		}).invoice_header;

		//Creating Lines Lines Array
		var invoicelines = [];
      var linNum = 1;
		while (grec.next()) {
			var lineInputs = {};
			lineInputs['invoice_line'] = grec;
			var lineResult = new sn_apm_sap_int.ApmSapUtils().executeAction('sn_apm_sap_int.generate_hana_invoice_line_object', {
				invoice_line: grec
			}).invoice_line;
			var erpLineNum = '';
			var costCenter_IRlines = new sn_apm_sap_int.ApmSapUtils().divideIRLinesBasedOnCostCenter(grec, lineResult);
			costCenter_IRlines.forEach(function(item) {
				item['INVOICE_DOC_ITEM'] = new sn_apm_sap_int.ApmSapUtils().checkPadding(linNum, 6);
				erpLineNum = erpLineNum + item['INVOICE_DOC_ITEM'] + ',';
				invoicelines.push(JSON.parse(JSON.stringify(item)));
				linNum++;
			});
			grec.erp_line_number = erpLineNum.slice(0, -1);
			grec.update();
		}
		outputs.invoice_lines = invoicelines;
	}
})(inputs, outputs);

